import React, { useEffect, useRef, useState } from 'react';

// Hero Section Component with GSAP Animations
const HeroSection = () => {
  const heroRef = useRef(null);
  const [timeLeft, setTimeLeft] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0
  });

  // Countdown timer - Set target date (adjust as needed)
  useEffect(() => {
    // Target date: January 15, 2026 at 00:00:00 UTC
    const targetDate = new Date('2026-01-15T00:00:00Z').getTime();

    const updateCountdown = () => {
      const now = new Date().getTime();
      const distance = targetDate - now;

      if (distance > 0) {
        setTimeLeft({
          days: Math.floor(distance / (1000 * 60 * 60 * 24)),
          hours: Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)),
          minutes: Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60)),
          seconds: Math.floor((distance % (1000 * 60)) / 1000)
        });
      }
    };

    updateCountdown();
    const interval = setInterval(updateCountdown, 1000);
    return () => clearInterval(interval);
  }, []);

  // GSAP Animations
  useEffect(() => {
    // Make sure GSAP is loaded
    if (typeof window === 'undefined' || !window.gsap) {
      console.warn('GSAP not loaded');
      return;
    }

    const gsap = window.gsap;
    const ScrollTrigger = window.ScrollTrigger;

    gsap.registerPlugin(ScrollTrigger);

    // Main timeline for intro sequence
    const mainTl = gsap.timeline({
      defaults: { ease: 'power3.out' }
    });

    // Set initial states
    gsap.set('#stageOverlay', { opacity: 1 });
    gsap.set('.cloud-layer', { opacity: 0 });
    gsap.set('#background-hills', { y: 100, opacity: 0 });
    gsap.set('#minables', { y: 100, opacity: 0, scale: 0.9 });
    gsap.set('#roadster', { x: '-150%', opacity: 0 });
    gsap.set('#cq-coin', { scale: 0, opacity: 0 });
    gsap.set('#cq-logo', { y: -100, opacity: 0 });
    gsap.set('#pre-mine-label', { y: 50, opacity: 0 });
    gsap.set('#countdown', { y: 30, opacity: 0 });
    gsap.set('#exhaust-flames', { opacity: 0, scale: 0.5 });

    // Animation sequence
    mainTl
      // 1. Fade out stage overlay
      .to('#stageOverlay', {
        opacity: 0,
        duration: 1.2,
        ease: 'power2.inOut'
      })
      // 2. Clouds fade in
      .to('.cloud-layer', {
        opacity: 1,
        duration: 0.8,
        stagger: 0.15
      }, '-=0.5')
      // 3. Background hills rise
      .to('#background-hills', {
        y: 0,
        opacity: 1,
        duration: 1,
        ease: 'power2.out'
      }, '-=0.6')
      // 4. Minables pop in
      .to('#minables', {
        y: 0,
        opacity: 1,
        scale: 1,
        duration: 0.7,
        ease: 'back.out(1.5)'
      }, '-=0.3')
      // 5. Roadster drives in
      .to('#roadster', {
        x: 0,
        opacity: 1,
        duration: 1.4,
        ease: 'power4.out'
      }, '-=0.3')
      // 6. Exhaust flames appear
      .to('#exhaust-flames', {
        opacity: 1,
        scale: 1,
        duration: 0.3
      }, '-=0.2')
      // 7. Coin appears
      .to('#cq-coin', {
        scale: 1,
        opacity: 1,
        duration: 1,
        ease: 'back.out(1.7)'
      }, '-=0.8')
      // 8. Logo drops in
      .to('#cq-logo', {
        y: 0,
        opacity: 1,
        duration: 0.8,
        ease: 'back.out(1.5)'
      }, '-=0.5')
      // 9. Pre-mine label fades up
      .to('#pre-mine-label', {
        y: 0,
        opacity: 1,
        duration: 0.6,
        ease: 'power2.out'
      }, '-=0.3')
      // 10. Countdown fades up
      .to('#countdown', {
        y: 0,
        opacity: 1,
        duration: 0.6,
        ease: 'power2.out'
      }, '-=0.3');

    // Idle animations (after main sequence)
    mainTl.call(() => {
      // Logo and coin group subtle float
      gsap.to('#hero-centerpiece', {
        y: -10,
        duration: 2,
        ease: 'sine.inOut',
        yoyo: true,
        repeat: -1
      });

      // Car idle bounce
      gsap.to('#roadster', {
        y: -3,
        duration: 0.8,
        ease: 'sine.inOut',
        yoyo: true,
        repeat: -1
      });

      // Exhaust flame flicker
      gsap.to('#exhaust-flames', {
        scaleX: 1.2,
        scaleY: 0.8,
        opacity: 0.7,
        duration: 0.15,
        ease: 'power1.inOut',
        yoyo: true,
        repeat: -1
      });

      // Cloud animations
      document.querySelectorAll('.cloud-layer').forEach((cloud, i) => {
        const direction = i % 2 === 0 ? 1 : -1;
        const distance = 20 + (i * 10);
        const duration = 15 + (i * 5);

        gsap.to(cloud, {
          x: distance * direction,
          duration: duration,
          ease: 'sine.inOut',
          yoyo: true,
          repeat: -1
        });
      });
    });

    // ScrollTrigger for parallax
    ScrollTrigger.create({
      trigger: '#cq-hero',
      start: 'top top',
      end: 'bottom top',
      scrub: 1,
      onUpdate: (self) => {
        const progress = self.progress;

        // Parallax layers at different speeds
        gsap.to('.cloud-layer.cloud-1', { y: progress * 100, overwrite: 'auto' });
        gsap.to('.cloud-layer.cloud-2', { y: progress * 80, overwrite: 'auto' });
        gsap.to('.cloud-layer.cloud-3', { y: progress * 60, overwrite: 'auto' });
        gsap.to('.cloud-layer.cloud-4', { y: progress * 40, overwrite: 'auto' });
        gsap.to('#background-hills', { y: progress * 50, overwrite: 'auto' });
        gsap.to('#minables', { y: progress * 20, overwrite: 'auto' });
        gsap.to('#roadster', { y: progress * 10, overwrite: 'auto' });
        gsap.to('#hero-centerpiece', { y: progress * -50, overwrite: 'auto' });
      }
    });

    // Cleanup
    return () => {
      ScrollTrigger.getAll().forEach(trigger => trigger.kill());
      gsap.killTweensOf('*');
    };
  }, []);

  const formatNumber = (num) => String(num).padStart(2, '0');

  return (
    <section 
      id="cq-hero" 
      ref={heroRef}
      className="relative w-full overflow-visible"
      style={{ 
        perspective: '1000px',
        height: 'clamp(500px, 85vh, 900px)'
      }}
    >
      {/* Stage Overlay - Fade from black */}
      <div 
        id="stageOverlay" 
        className="absolute inset-0 bg-black z-50 pointer-events-none"
        style={{ willChange: 'opacity' }}
      />

      {/* Sky Background */}
      <div 
        id="sky" 
        className="absolute inset-0"
        style={{ backgroundColor: '#74FDE7' }}
      />

      {/* Clouds Layer */}
      <div className="absolute inset-0 overflow-hidden">
        <img 
          src="/assets_cqTGE/cloud1.png" 
          alt="" 
          className="cloud-layer cloud-1 absolute"
          style={{ 
            top: '5%', 
            left: '5%', 
            width: 'clamp(80px, 15vw, 150px)',
            willChange: 'transform, opacity'
          }}
        />
        <img 
          src="/assets_cqTGE/cloud2.png" 
          alt="" 
          className="cloud-layer cloud-2 absolute"
          style={{ 
            top: '8%', 
            right: '10%', 
            width: 'clamp(60px, 12vw, 120px)',
            willChange: 'transform, opacity'
          }}
        />
        <img 
          src="/assets_cqTGE/cloud1.png" 
          alt="" 
          className="cloud-layer cloud-3 absolute"
          style={{ 
            top: '15%', 
            left: '25%', 
            width: 'clamp(50px, 10vw, 100px)',
            willChange: 'transform, opacity'
          }}
        />
        <img 
          src="/assets_cqTGE/cloud2.png" 
          alt="" 
          className="cloud-layer cloud-4 absolute"
          style={{ 
            top: '12%', 
            right: '30%', 
            width: 'clamp(40px, 8vw, 80px)',
            willChange: 'transform, opacity'
          }}
        />
      </div>

      {/* Background Hills */}
      <div 
        id="background-hills"
        className="absolute bottom-0 left-0 right-0"
        style={{ 
          height: 'clamp(180px, 40vh, 400px)',
          willChange: 'transform, opacity'
        }}
      >
        <img 
          src="/assets_cqTGE/background_hills.svg" 
          alt="" 
          className="w-full h-full object-cover object-bottom"
          style={{ objectPosition: 'center bottom' }}
        />
      </div>

      {/* Mining Items / Foreground Elements - Behind car, now includes gradient, taller */}
      <div 
        id="minables"
        className="absolute bottom-0 left-0 right-0"
        style={{ 
          height: 'clamp(180px, 40vh, 350px)',
          width: '100%',
          willChange: 'transform, opacity',
          zIndex: 10
        }}
      >
        <img 
          src="/assets_cqTGE/mining_items_gradient.png" 
          alt="" 
          className="w-full h-full object-cover object-bottom"
          style={{ overflow: 'visible' }}
        />
      </div>

      {/* Foreground - Roadster - Centered horizontally, Bottom pinned to ground */}
      <div 
        id="roadster"
        className="absolute left-1/2"
        style={{ 
          bottom: '-30px',
          transform: 'translateX(-50%)',
          width: 'clamp(200px, 45vw, 420px)',
          willChange: 'transform, opacity',
          transformOrigin: 'center bottom',
          zIndex: 20
        }}
      >
        {/* Exhaust Flames */}
        <div 
          id="exhaust-flames"
          className="absolute"
          style={{
            bottom: '20%',
            left: '-15%',
            width: '20%',
            height: '15%',
            background: 'linear-gradient(90deg, #FF6B35, #FFD700, transparent)',
            borderRadius: '50% 0 0 50%',
            filter: 'blur(2px)',
            willChange: 'transform, opacity'
          }}
        />
        <img 
          src="/assets_cqTGE/roadster.png" 
          alt="Critters Quest Roadster" 
          className="w-full h-auto"
        />
      </div>

      {/* Hero Centerpiece - Logo, Coin, Text - Aligned to top */}
      <div 
        id="hero-centerpiece"
        className="absolute left-0 right-0 top-0 flex flex-col items-center pointer-events-none"
        style={{ 
          paddingTop: 'clamp(15px, 2vh, 40px)',
          willChange: 'transform',
          zIndex: 15
        }}
      >
        {/* Logo */}
        <div 
          id="cq-logo"
          className="relative"
          style={{ 
            width: 'clamp(180px, 35vw, 330px)',
            marginBottom: 'clamp(6px, 1vh, 15px)',
            willChange: 'transform, opacity'
          }}
        >
          <img 
            src="/assets_cqTGE/critters_quest_logo.png" 
            alt="Critters Quest" 
            className="w-full h-auto"
          />
        </div>

        {/* Coin - proportional to logo (about 55% of logo width) */}
        <div 
          id="cq-coin"
          className="relative"
          style={{ 
            width: 'clamp(100px, 19vw, 180px)',
            willChange: 'transform, opacity'
          }}
        >
          <img 
            src="/assets_cqTGE/Coin.svg" 
            alt="QUEST Token" 
            className="w-full h-auto"
          />
        </div>

        {/* Pre-Mine Label - capped font size */}
        <div 
          id="pre-mine-label"
          className="mt-2 md:mt-3"
          style={{ willChange: 'transform, opacity' }}
        >
          <h2 
            className="text-black font-bold tracking-wide"
            style={{ 
              fontFamily: "'Comic Sans MS', 'Comic Sans', cursive",
              fontSize: 'clamp(1.8rem, 5vw, 3.2rem)',
              color: '#000000',
              letterSpacing: '0.02em'
            }}
          >
            PRE-MINE
          </h2>
        </div>

        {/* Countdown Timer - capped font size */}
        <div 
          id="countdown"
          className="mt-1"
          style={{ willChange: 'transform, opacity' }}
        >
          <div 
            className="flex items-center justify-center"
            style={{ 
              fontFamily: "'Comic Sans MS', 'Comic Sans', cursive",
              fontSize: 'clamp(1.4rem, 4vw, 2.6rem)',
              color: '#000000',
              fontWeight: 'bold'
            }}
          >
            {timeLeft.days > 0 && (
              <>
                <span className="countdown-segment">{formatNumber(timeLeft.days)}</span>
                <span className="countdown-separator">:</span>
              </>
            )}
            <span className="countdown-segment">{formatNumber(timeLeft.hours)}</span>
            <span className="countdown-separator">:</span>
            <span className="countdown-segment">{formatNumber(timeLeft.minutes)}</span>
            <span className="countdown-separator">:</span>
            <span className="countdown-segment">{formatNumber(timeLeft.seconds)}</span>
          </div>
          {timeLeft.days > 0 && (
            <div className="text-center text-xs md:text-sm text-gray-800 mt-1 font-medium" style={{ fontFamily: "'Comic Sans MS', 'Comic Sans', cursive" }}>
              {timeLeft.days} Days · {timeLeft.hours}h · {timeLeft.minutes}m · {timeLeft.seconds}s
            </div>
          )}
        </div>
      </div>

      {/* Scroll Indicator */}
      <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 z-20 animate-bounce">
        <div className="w-5 h-8 border-2 border-gray-800/50 rounded-full flex justify-center">
          <div className="w-1 h-2 bg-gray-800/50 rounded-full mt-1.5 animate-pulse" />
        </div>
      </div>

      {/* CSS for hero-specific styles */}
      <style>{`
        #cq-hero {
          font-family: 'Comic Sans MS', 'Comic Sans', cursive;
        }

        #cq-hero img {
          -webkit-user-drag: none;
          user-select: none;
        }

        .countdown-segment {
          display: inline-block;
          min-width: 1.5em;
          text-align: center;
        }

        .countdown-separator {
          opacity: 0.7;
        }


        /* Car always centered */
        #roadster {
          left: 50% !important;
          transform: translateX(-50%);
        }

        /* Responsive adjustments for tall/narrow screens */
        @media (max-aspect-ratio: 3/4) {
          #cq-hero {
            height: clamp(450px, 70vh, 700px) !important;
          }

          #background-hills {
            height: clamp(150px, 30vh, 300px) !important;
          }

          #roadster {
            bottom: 0 !important;
            width: clamp(180px, 55vw, 320px) !important;
          }
        }

        /* Mobile portrait */
        @media (max-width: 480px) {
          #cq-hero {
            height: clamp(400px, 65vh, 600px) !important;
          }

          #hero-centerpiece {
            padding-top: clamp(10px, 2vh, 25px) !important;
          }

          #roadster {
            width: clamp(160px, 60vw, 280px) !important;
            bottom: 0 !important;
          }
        }

        /* Tablet */
        @media (min-width: 481px) and (max-width: 1024px) {
          #roadster {
            width: clamp(220px, 45vw, 380px) !important;
          }
        }

        /* Lock sizes at 768px breakpoint - elements don't grow beyond this */
        @media (min-width: 768px) {
          #cq-logo {
            width: 268.8px !important; /* 35vw at 768px */
          }

          #cq-coin {
            width: 145.92px !important; /* 19vw at 768px */
          }

          #pre-mine-label h2 {
            font-size: 2.4rem !important; /* 5vw at 768px ≈ 38.4px */
          }

          #countdown > div:first-child {
            font-size: 1.92rem !important; /* 4vw at 768px ≈ 30.72px */
          }

          #roadster {
            width: 640px !important;
          }
        }

        /* 4K / Widest breakpoint */
        @media (min-width: 2560px) {
          #minables {
            // max-width: 3840px !important;
            bottom: -40px !important;
          }
        }
      `}</style>
    </section>
  );
};

export default HeroSection;
